import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators} from '@angular/forms';
import { Validator} from 'mongoose';
import { BloqueoService } from 'src/app/servicios/bloqueo.service';

@Component({
  selector: 'app-bloqueos',
  templateUrl: './bloqueos.component.html',
  styleUrls: ['./bloqueos.component.css']
})
export class BloqueosComponent implements OnInit{
  bloqueoForm!:FormGroup;

  constructor(private readonly fb:FormBuilder){};

  ngOnInit() {
    this.bloqueoForm=this.initForm();

  }
  onSubmit():void{
    console.log(this.bloqueoForm.value);
  } 
  initForm():FormGroup{
    return this.fb.group({
      cantidad:['2',Validators.required],
      fecha:['',Validators.required],
      medico:['',Validators.required]
    })
  }
  }
